import React, { useState } from 'react';
import './styles.css';

function App() {
  const [page, setPage] = useState('upload'); // "upload" or "results"
  const [isFading, setIsFading] = useState(false);

  const handleUpload = () => {
    // 1) Start fade-out
    setIsFading(true);

    // 2) Wait ~500ms (the length of the fade-out), then switch pages
    setTimeout(() => {
      setPage('results');
      setIsFading(false);
    }, 500); // match this with your CSS transition duration
  };

  // You can do the same approach if you want a fade back to "upload"
  // for a "Back" button or something else.

  // We'll pick which content to render
  const content =
    page === 'upload' ? (
      <div className="upload-card">
        <h1 className="upload-title">GeneSight Upload Page</h1>
        <p className="upload-subtitle">
          Click the button below to upload your files for analysis.
        </p>
        <div className="upload-row">
          <input type="file" className="file-input" />
          <button className="upload-button" onClick={handleUpload}>
            Upload Files Here Now
          </button>
        </div>
      </div>
    ) : (
      <div className="upload-card">
        <h1 className="upload-title">Here's What We Found:</h1>
        <p className="upload-subtitle">Your results will be displayed here!</p>
      </div>
    );

  return (
    <div className="main-background">
      <div className={`container ${isFading ? 'fade-out' : ''}`}>
        {content}
      </div>
    </div>
  );
}

export default App;
