// App.js
import React, { useState, useEffect } from 'react';
import SplashScreen from './SplashScreen';
import './styles.css';

function App() {
  // State: controls splash screen visibility
  const [showSplash, setShowSplash] = useState(true);
  // State: triggers fade-out animation
  const [fadeClass, setFadeClass] = useState('');
  // State: which page to show after splash
  const [page, setPage] = useState('upload');

  useEffect(() => {
    // Show splash for 3 seconds, then fade out for 1s
    const timer = setTimeout(() => {
      setFadeClass('fade-out'); // trigger fade-out CSS
      setTimeout(() => {
        setShowSplash(false);
      }, 1000);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  const handleUpload = () => {
    setPage('results');
  };

  return (
    <div className="App">
      {showSplash ? (
        // Splash wrapper for fade-out
        <div className={`splash-wrapper ${fadeClass}`}>
          <SplashScreen />
        </div>
      ) : (
        // Main layout after splash
        <div className="page-wrapper">
          {/* HEADER */}
          <header className="header">
            <h1 className="app-title">GeneSight</h1>
          </header>

          {/* MAIN CONTENT (with hologram overlay) */}
          <div className="main-content">
            {/* AI/Hologram in the center, behind sections */}
            <div className="hologram-overlay">
              <div className="hologram-dna"></div>
            </div>

            {/* Page content */}
            {page === 'upload' ? (
              <div className="sections-container">
                {/* Left column: file selection */}
                <div className="left-section">
                  <h2>Select File</h2>
                  <input type="file" className="file-input" />
                </div>

                {/* Right column: upload button */}
                <div className="right-section">
                  <h2>Ready to Upload</h2>
                  <button className="upload-button" onClick={handleUpload}>
                    Upload Files Now
                  </button>
                </div>
              </div>
            ) : (
              // Results page
              <div className="sections-container">
                <h2>Hereâ€™s What We Found:</h2>
                <p>Your results will be displayed here!</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
