// App.js
import React, { useState, useEffect } from 'react';
import SplashScreen from './SplashScreen';
import './styles.css'; // Your existing styles for the upload/results page

function App() {
  // State to track whether to show the splash screen
  const [showSplash, setShowSplash] = useState(true);
  // State to manage fade classes for a smooth transition
  const [fadeClass, setFadeClass] = useState('fade-enter');

  // Timer to handle splash duration and fade transition
  useEffect(() => {
    // Start with fade-in (you can adjust the timing as desired)
    setFadeClass('fade-enter-active');

    // After 2 seconds, start fade-out transition
    const splashTimer = setTimeout(() => {
      setFadeClass('fade-exit');
      // After the fade-out completes (~800ms), hide the splash screen
      setTimeout(() => {
        setShowSplash(false);
      }, 800);
    }, 2000);

    return () => clearTimeout(splashTimer);
  }, []);

  // Existing state for switching between upload and results pages
  const [page, setPage] = useState('upload');

  const handleUpload = () => {
    // When the upload button is clicked, change page to 'results'
    setPage('results');
  };

  return (
    <div className="App">
      {showSplash ? (
        <div className={fadeClass}>
          <SplashScreen />
        </div>
      ) : (
        <div className="container">
          {page === 'upload' ? (
            <>
              <h1>GeneSight Upload Page</h1>
              <p>Click the button below to upload your files for analysis.</p>
              <input type="file" className="file-input" />
              <button className="upload-button" onClick={handleUpload}>
                Upload Files Here Now
              </button>
            </>
          ) : (
            <>
              <h1>Here's What We Found:</h1>
              <p>Your results will be displayed here!</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
