import React, { useState, useEffect } from 'react';
import './styles.css';

function App() {
  const [page, setPage] = useState('upload');  // "upload" or "results"

  // For transitions:
  const [fadeOut, setFadeOut] = useState(false);
  const [fadeIn, setFadeIn] = useState(false);

  // 1) We want an initial fade in on page load
  //    So we'll set fadeIn=true once at mount:
  useEffect(() => {
    setFadeIn(true);

    // after 500ms, remove fadeIn so we don't keep the class forever
    const timer = setTimeout(() => {
      setFadeIn(false);
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  const handleUpload = () => {
    // fade out
    setFadeOut(true);

    setTimeout(() => {
      // switch page
      setPage('results');
      // remove fadeOut
      setFadeOut(false);

      // fade in new page
      setFadeIn(true);
      setTimeout(() => {
        setFadeIn(false);
      }, 500);
    }, 500);
  };

  // Optional: allow going back to see the fade again
  const handleBack = () => {
    setFadeOut(true);
    setTimeout(() => {
      setPage('upload');
      setFadeOut(false);
      setFadeIn(true);
      setTimeout(() => {
        setFadeIn(false);
      }, 500);
    }, 500);
  };

  // Decide content
  let content;
  if (page === 'upload') {
    content = (
      <div className="content-card">
        <h1 className="page-title">GeneSight Upload Page</h1>
        <p className="page-subtitle">
          Click the button below to upload your files for analysis.
        </p>
        <div className="row">
          <input type="file" className="file-input" />
          <button className="upload-button" onClick={handleUpload}>
            Upload Files Here Now
          </button>
        </div>
      </div>
    );
  } else {
    content = (
      <div className="content-card">
        <h1 className="page-title">Here's What We Found:</h1>
        <p className="page-subtitle">Your results will be displayed here!</p>
        <button className="upload-button" onClick={handleBack}>
          Back to Upload
        </button>
      </div>
    );
  }

  return (
    <div className="app-background">
      {/* container gets fade-out/fade-in classes */}
      <div className={`container ${fadeOut ? 'fade-out' : ''} ${fadeIn ? 'fade-in' : ''}`}>
        {content}
      </div>
    </div>
  );
}

export default App;
