// App.js
import React, { useState, useEffect } from 'react';
import SplashScreen from './SplashScreen';
import './styles.css';

function App() {
  const [showSplash, setShowSplash] = useState(true);
  const [fadeClass, setFadeClass] = useState('');
  const [page, setPage] = useState('upload');

  useEffect(() => {
    // Keep splash for 3s, then fade out
    const timer = setTimeout(() => {
      setFadeClass('fade-out');
      setTimeout(() => {
        setShowSplash(false);
      }, 1000);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  const handleUpload = () => {
    setPage('results');
  };

  return (
    <div className="App">
      {/* Splash screen on top */}
      {showSplash && (
        <div className={`splash-wrapper ${fadeClass}`}>
          <SplashScreen />
        </div>
      )}

      <div className="page-wrapper">
        <header className="header-bar">
          <h1 className="app-title">GeneSight</h1>
        </header>

        <div className="main-content">
          <div className="hologram-overlay">
            <div className="hologram-dna"></div>
          </div>

          {page === 'upload' ? (
            <div className="sections-container">
              <div className="section-left">
                <h2>Select File</h2>
                <label className="file-label" htmlFor="file-upload">
                  Choose File
                </label>
                <input id="file-upload" type="file" className="file-input" />
              </div>
              <div className="section-right">
                <h2>Ready to Upload</h2>
                <button className="upload-button" onClick={handleUpload}>
                  Upload Files Now
                </button>
              </div>
            </div>
          ) : (
            <div className="sections-container results-section">
              <h2>Hereâ€™s What We Found</h2>
              <p>Your results will be displayed here.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
