import React, { useState, useEffect } from 'react';
import SplashScreen from './SplashScreen';
import dnaHologram from './assets/dnaHologram.mp4';  // <-- import your local video
import './styles.css';

function App() {
  // State for splash
  const [showSplash, setShowSplash] = useState(true);
  const [fadeClass, setFadeClass] = useState('');
  const [page, setPage] = useState('upload');

  useEffect(() => {
    // Timer to fade out splash
    const timer = setTimeout(() => {
      setFadeClass('fade-out');
      setTimeout(() => {
        setShowSplash(false);
      }, 1000);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  const handleUpload = () => {
    setPage('results');
  };

  return (
    <div className="App">
      {showSplash ? (
        <div className={fadeClass}>
          <SplashScreen />
        </div>
      ) : (
        <div className="container">
          <h1>GeneSight</h1>
          {page === 'upload' ? (
            <>
              <h2>Upload Your Gene Expression Data</h2>
              <p>Get a quick and explainable risk assessment for disease prediction.</p>

              <input type="file" className="file-input" />
              <button className="upload-button" onClick={handleUpload}>
                Upload Files Now
              </button>

              {/* Hologram Section with the looping MP4 */}
              <div className="hologram-container">
                <video 
                  autoPlay 
                  loop 
                  muted 
                  playsInline
                  className="hologram-video"
                >
                  <source src={dnaHologram} type="video/mp4" />
                  {/* fallback text */}
                  Your browser does not support the video tag.
                </video>
              </div>
            </>
          ) : (
            <>
              <h2>Here’s What We Found</h2>
              <p>Your results will be displayed here once the analysis is complete.</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
