import React, { useState, useEffect } from 'react';
import SplashScreen from './SplashScreen';
import './styles.css';

function App() {
  // Controls splash screen visibility
  const [showSplash, setShowSplash] = useState(true);
  // Controls fade-out class
  const [fadeClass, setFadeClass] = useState('');
  // Decides if we’re on upload or results page
  const [page, setPage] = useState('upload');

  useEffect(() => {
    // Show splash for 3 seconds
    const timer = setTimeout(() => {
      // Trigger fade out
      setFadeClass('fade-out');
      // Hide splash after fade-out completes
      setTimeout(() => {
        setShowSplash(false);
      }, 1000);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  const handleUpload = () => {
    setPage('results');
  };

  return (
    <div className="App">
      {/* If still in splash state, show the splash screen with fadeClass */}
      {showSplash ? (
        <div className={fadeClass}>
          <SplashScreen />
        </div>
      ) : (
        <div className="container">
          <h1>GeneSight</h1>
          {page === 'upload' ? (
            <>
              <h2>Upload Your Gene Expression Data</h2>
              <p>Get a quick and explainable risk assessment for disease prediction.</p>
              <input type="file" className="file-input" />
              <button className="upload-button" onClick={handleUpload}>
                Upload Files Now
              </button>
              <div className="hologram-container">
                {/* Replace with an actual image or a futuristic animation */}
                {/* <img src="path-to-your-hologram.png" alt="Live Genome Hologram" /> */}
                <span>Live Genome Hologram</span>
              </div>
            </>
          ) : (
            <>
              <h2>Here’s What We Found</h2>
              <p>Your results will be displayed here once the analysis is complete.</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
