import React, { useState } from 'react';
import './styles.css'; // or "./App.css" - whichever you prefer

function App() {
  const [page, setPage] = useState('upload'); // "upload" or "results"
  // We'll use two pieces of state to handle fade-out/fade-in transitions
  const [fadeOut, setFadeOut] = useState(false);
  const [fadeIn, setFadeIn] = useState(false);

  const handleUpload = () => {
    // 1) Trigger fade-out for the current page
    setFadeOut(true);

    // 2) Wait for fade-out to finish (e.g. 500ms),
    //    then switch pages and trigger fade-in on the new page
    setTimeout(() => {
      setPage('results');
      setFadeOut(false);
      setFadeIn(true);

      // 3) After a small delay, remove fadeIn so it's not stuck in that class
      //    This helps if you come back to the page or do multiple transitions
      setTimeout(() => {
        setFadeIn(false);
      }, 500); // match fade-in duration
    }, 500); // match fade-out duration
  };

  // For demonstration, let's also allow going BACK from results → upload
  // so you can see the fade both ways. This is optional:
  const handleBack = () => {
    setFadeOut(true);
    setTimeout(() => {
      setPage('upload');
      setFadeOut(false);
      setFadeIn(true);
      setTimeout(() => {
        setFadeIn(false);
      }, 500);
    }, 500);
  };

  // Decide what content to render based on 'page'
  let content;
  if (page === 'upload') {
    content = (
      <div className="content-card">
        <h1 className="page-title">GeneSight Upload Page</h1>
        <p className="page-subtitle">Click the button below to upload your files for analysis.</p>
        <div className="row">
          <input type="file" className="file-input" />
          <button className="upload-button" onClick={handleUpload}>
            Upload Files Here Now
          </button>
        </div>
      </div>
    );
  } else {
    // results page
    content = (
      <div className="content-card">
        <h1 className="page-title">Here's What We Found:</h1>
        <p className="page-subtitle">Your results will be displayed here!</p>
        <button className="upload-button" onClick={handleBack}>
          Back to Upload
        </button>
      </div>
    );
  }

  return (
    <div className="app-background">
      {/* container gets classes for fade-out or fade-in */}
      <div className={`container ${fadeOut ? 'fade-out' : ''} ${fadeIn ? 'fade-in' : ''}`}>
        {content}
      </div>
    </div>
  );
}

export default App;
